    const Scanner=new html5QrcodeScanner('reader',{
        qrbox:{
          width:250,
          height:250,
        },
        fps:5,
      })
      Scanner.render(success,error);
      function success(){
        Scanner.clear();
        setScanResult(result);
      }
      function  error(err){
        console.warn(err)
      }
      return (
        <>
          <QrReader
            onResult={(result, error) => {
              if (!!result) {
                setData(result?.text);
              }
    
              if (!!error) {
                console.info(error);
              }
            }}
